var text = sig("creative|coding|club");
var TextBox = () => {
  return h("div", {
    style: {
      width: "90vw",
      height: "50px"
    }
  }, h("input", {
    style: {
      all: "unset",
      border: ".1px dotted yellow",
      color: "yellow"
    },
    type: "text",
    oninput: (e2) => {
      console.log(e2.target.value);
      text.set(e2.target.value);
    }
  }));
};

var App = () => {
  const split = mem(() => text().split(""));
  return html`
    div [style=display:flex;flex-wrap:wrap;justify-content:center;font-family:monospace]
      each of ${split} as ${Grid}
  `
};

var Grid = (letter) => {
  const alphabet = "abcdefghijklmnopqrstuvwxyz10".split("");
  const style2 = mem(() => {
    return {
      width: letter === "|" ? "100vw" : "300px",
      height: letter === "|" ? "30px" : "300px",
      display: "flex",
      "justify-content": "center",
      "align-items": "center",
      position: "relative"
    };
  });

  return h("div", { style: style2 }, 
    () => h("video", {
      src: `./vids/${letter}.mp4`,
      autoplay: true,
      loop: true,
      style: {
        width: "100%",
        height: "100%",
        position: "absolute",
        "z-index": -1
      }
  }),  
     () => h("p",
             {style: {color: "yellow", "font-size": "15px", "text-transform": "uppercase"}},
             letter != "|" ? letter: "")
  );
};

render(App, document.body);

let all_videos = document.querySelectorAll("video")
Array.from(all_videos).forEach((vid)=> {
  vid.playbackRate = .5
  // vid.style.filter = `invert(.2)`
})

