  let time = "4pm"
  let font

  let half_w = () => p.width / 2
  let half_h = () => p.height / 2

  p.preload = () =>{
    font = p.loadFont("/fonts/hermit.otf")
  }

  p.setup = () => {
    p.createCanvas(1500, 1500)
